<template>
  <div class="vnt-autosuggest">
    <label v-if="label"
           class="vnt-autosuggest__label"
           :class="{'vnt-autosuggest__label--disabled': disabled}">
      {{ label }}
    </label>

    <div class="vnt-autosuggest__control"
         role="combobox">

      <input class="vnt-autosuggest__input"
             type="text"
             :disabled="disabled"
             :placeholder="placeholder"
             :value="value"
             v-bind="$attrs"
             @input="input" />

      <vnt-dropdown-options :options="hints"
                            :opened.sync="showHints"
                            @on-select="selectHint" />

    </div>

  </div>
</template>

<style lang="scss">
@import '../../scss/mixins/component';

.vnt-autosuggest {
  @include component-base();

  color: #000100;
  display: block;
  margin: 12px 0;
}

.vnt-autosuggest__label {
  display: block;
  padding: 10px 0;
  line-height: 20px;

  &--disabled {
    color: #999;
  }
}

.vnt-autosuggest__control {
  display: inline-block;
  position: relative;

  &::after {
    content: '';
    display: inline-block;
    background-image: url('data:image/svg+xml;base64,');
    background-repeat: no-repeat;
    width: 13px;
    height: 13px;
    position: relative;
    margin-left: -24px;
  }
}

.vnt-autosuggest__input {
  @include component-input();

  padding: 0 30px 0 10px;

  &::-ms-clear {
    display: none;
}
}
</style>

<script>
import VntDropdownOptions from '../dropdown/Options.vue';

export default {
  name: 'VntAutosuggest',

  components: {
    VntDropdownOptions
  },

  props: {
    disabled: {
      type: Boolean,
      default: false
    },
    label: {
      type: String,
      default: ''
    },
    placeholder: {
      type: String,
      default: ''
    },
    options: {
      type: Array,
      default() {
        return [];
      }
    },
    value: {
      type: String,
      default: ''
    }
  },

  data() {
    return {
      hints: [],
      showHints: false,
    };
  },

  watch: {
    hints(hints) {
      this.showHints = hints.length > 0;
    }
  },

  methods: {
    input({target, value}) {
      const query = value || target.value;
      this.$emit('input', query);
      this.lookup(query);
    },

    lookup(query = '') {
      const lookupQuery = query.toLocaleLowerCase();
      this.hints = this.options
        .filter((option) =>
          String(option).toLocaleLowerCase().indexOf(lookupQuery) > -1)
        .sort();
    },

    selectHint(option) {
      if (!this.disabled) {
        this.$emit('input', option);
      }
    }

  }
};
</script>
